import React,{Component} from 'react';
import logo from './logo.svg';
import './App.css';

export default class Records extends Component{
	constructor(){
		super()
		this.Addrecord=this.Addrecord.bind(this)
		this.delete=this.delete.bind(this)

		this.state={
			records:[
				{
					"regno":1,
					"name":"shafeeq",
					"marks":89
				},
				{
					"regno":2,
					"name":"P3ter",
					"marks":90
				},
				{
					"regno":3,
					"name":"James Bond",
					"marks":190
				},
				{
					"regno":4,
					"name":"James Bond2",
					"marks":10
				},

			]
			}
	}
	delete(r){
		this.setState({
			records: this.state.records.filter(function(data){
				if (data.regno==r){
					return false
				}else{
					return true
				}
			})
		})
	}
	Addrecord(){
		this.setState({
			records:this.state.records.concat({
					"regno":121,
					"name":"Pater2",
					"marks":902
				})	
		})
	}
	render(){
		return (
			<div>
					<br/>
					<br/>	
					<input type="button" value="New Record" onClick={this.Addrecord}/>
					<br/>
					Total Number of Records( { this.state.records.length} )
					<table>
						<tr>
							<td> regno </td>
							<td> name </td>
							<td> marks </td>
						</tr>	
				 	{
				 		this.state.records.map(data	=>
				 		<tr> 
				 			<td> {data.regno}  </td>
				 			<td> {data.name}  </td>
				 			<td> {data.marks} </td>
				 			<td> {data.marks*100/150} </td>
				 			<td> <input type="button" value="delete" onClick={() => this.delete(data.regno)} /> </td>
				 		</tr>
				 		)
				 	}
				 	</table>
			</div>
		)
	}
}
