import 'bootstrap/dist/css/bootstrap.css';
import React,{Component} from 'react';
import './App.css';
import { Link } from 'react-router-dom';
import Navbar from './Navbar.js'
import Footer from './Footer.js'
const nodemailer = require("nodemailer");

export default class Contact extends Component{

    constructor(props){
        super(props)
        this.setState({
            transporter: nodemailer.createTransport({
                service: 'gmail',
                auth: {
                  user: 'QACinemaJun2020@gmail.com',
                  pass: 'junsoftware'
                }
              }),
              mailOptions: {
                from: 'youremail@gmail.com',
                to: 'myfriend@yahoo.com',
                subject: 'Sending Email using Node.js',
                text: 'That was easy!'
              },

        })
    }


    sendMessage = () =>{
        console.log("button pressed")
        this.state.transporter.sendMail(this.state.mailOptions, function(error, info){
            if (error) {
              console.log(error);
            } else {
              console.log('Email sent: ' + info.response);
            }
          });

    }



    render(){
        return(
            <div>
                <Navbar></Navbar>
                <div class="container">
                    <h1 class="brand"><span>Acme</span> Web Design</h1>
                    <div class="wrapper animated bounceInLeft">
                        <div class="company-info">
                            <h3>Acme Web Design</h3>
                            <ul>
                                <li><i class="fa fa-road"></i> 44 Something st</li>
                                <li><i class="fa fa-phone"></i> (555) 555-5555</li>
                                <li><i class="fa fa-envelope"></i> test@acme.test</li>
                            </ul>
                        </div>
                        <div class="contact">
                            <h3>Email Us</h3>
                        <form onSubmit={this.sendMessage}>
                            <p>
                                <label>Name</label>
                                <input type="text" name="name"></input>
                            </p>
                            <p>
                                <label>Company</label>
                                <input type="text" name="company"></input>
                            </p>
                            <p>
                                <label>Email Address</label>
                                <input type="email" name="email"></input>
                            </p>
                            <p>
                                <label>Phone Number</label>
                                <input type="text" name="phone"></input>
                            </p>
                            <p class="full">
                                <label>Message</label>
                                <textarea name="message" rows="5"></textarea>
                            </p>
                            <p class="full">
                                <button type="submit">Submit</button>
                            </p>
                        </form>
                        </div>
                    </div>
                </div>
                <Footer></Footer>
            </div>
        )
    }
}
