import 'bootstrap/dist/css/bootstrap.css';
import React,{Component} from 'react';
import './App.css';


export default class Homebody extends Component{
	constructor(){
		super()
		// this.Addrecord=this.Addrecord.bind(this)
		// this.delete=this.delete.bind(this)

		this.state={
			records:[
				{
					"movieID":1,
					"name":"Harry Potter",
					"image": "media/harrypotter.jpg"
				},
				{
					"movieID":2,
					"name":"The Avengers",
					"image":"media/avengers.jpg"
				},
				{
                    "movieID":3,
					"name":"Batman",
					"image":"media/batman.png"
				},
				{
					"movieID":4,
					"name":"Avatar",
					"image":"media/avatar.jpg"
                },
                {
					"movieID":4,
					"name":"Avatar",
					"image":"media/avatar.jpg"
                },
                {
					"movieID":4,
					"name":"Avatar",
					"image":"media/avatar.jpg"
                },
                {
					"movieID":2,
					"name":"The Avengers",
					"image":"media/avengers.jpg"
                },
                {
					"movieID":1,
					"name":"Harry Potter",
					"image": "media/harrypotter.jpg"
				}     
			]
			}
    }

	// delete(r){
	// 	this.setState({
	// 		records: this.state.records.filter(function(data){
	// 			if (data.regno==r){
	// 				return false
	// 			}else{
	// 				return true
	// 			}
	// 		})
	// 	})
	// }
	// Addrecord(){
	// 	this.setState({
	// 		records:this.state.records.concat({
	// 				"regno":121,
	// 				"name":"Pater2",
	// 				"marks":902
	// 			})	
	// 	})
	// }
	render(){
		return (
            <div>
            <Navbar bg="light" expand="lg">
            <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
                <Nav className="mr-auto">
                    <Nav.Link href="#home">Home</Nav.Link>
                    <Nav.Link href="#link">Link</Nav.Link>
                    <NavDropdown title="Dropdown" id="basic-nav-dropdown">
                        <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                        <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
                        <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                        <NavDropdown.Divider />
                        <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
                    </NavDropdown>
                </Nav>
                <Form inline>
                    <FormControl type="text" placeholder="Search" className="mr-sm-2" />
                    <Button variant="outline-success">Search</Button>
                </Form>
            </Navbar.Collapse>
        </Navbar>
			<div style={{backgroundColor: "black", color: "white"}}>
                <center><img src="/media/wallpaper.jpg" style={{hight: "600px", width: "1400px"}} alt="Wallpaper"/></center>
                <div class="row row-cols-4">
                {
                this.state.records.map(data	=>
                    <div class="card" style={{width: "18rem", hight: "40rem", backgroundColor: "black"}} >
                        <img src={data.image} class="card-img-top" alt={data.name}/>
                        <div class="card-body">
                            <p class="card-text">{data.name}</p>
                        </div>
                    </div>
                )
                }
                </div>
			</div>
            </div>
		)
	}
}
