import 'bootstrap/dist/css/bootstrap.css';
import React,{Component} from 'react';
import './App.css';
import { Link } from 'react-router-dom';
import Navbar from './Navbar.js'
import Footer from './Footer.js'
import nodemailer from 'nodemailer';
import axios from 'axios';

export default class Contact extends Component{

    state={
        name:'',
        lastname:'',
        email:'',
        message:'',
        sent:false,
        buttonText: 'Send Message',

    }

    handleName = (e)=>{
        this.setState({
          name:e.target.value
        })
        }

        handleLastname = (e)=>{
          this.setState({
            lastname:e.target.value
          })
          }

        handleEmail = (e)=>{
          this.setState({
            email:e.target.value
          })
          }

        handleMessage = (e)=>{
          this.setState({
            message:e.target.value
          })
          }

          formSubmit=(e)=>{
            e.preventDefault();
            let data = {
              name:this.state.name,
              lastname:this.state.lastname,
              email:this.state.email,
              message:this.state.message
            }
            axios.post('/api/forma',data)
            .then(res=>{
              this.setState({
                sent:true,
              },this.resetForm())
            })
            .catch(()=>{
              console.log('message not send');

            })


           }

           resetForm=()=>{
            this.setState({
              name:'',
              lastname:'',
              message:'',
              email:'',

            })

            setTimeout(()=>{
              this.setState({
                sent:false,



              })
            },3000)
          }


    sendMessage = () =>{
        var transporter = nodemailer.createTransport({
            service: 'gmail',
            auth: {
              user: 'QACinemaJun2020@gmail.com',
              pass: 'junsoftware'
            }
          });

          var mailOptions = {
            from: 'QACinemaJun2020@gmail.com',
            to: 'ahmad_naeem1999@hotmail.com',
            subject: 'Sending Email using Node.js',
            text: 'That was easy!'
          };

        console.log("button pressed")
        transporter.sendMail(mailOptions, function(error, info){
            if (error) {
              console.log(error);
            } else {
              console.log('Email sent: ' + info.response);
            }
          });

    }



    render(){
        return(
            <div>
                <Navbar></Navbar>
                <div class="container">
                    <h1 class="brand"><span>Acme</span> Web Design</h1>
                    <div class="wrapper animated bounceInLeft">
                        <div class="company-info">
                            <h3>Acme Web Design</h3>
                            <ul>
                                <li><i class="fa fa-road"></i> 44 Something st</li>
                                <li><i class="fa fa-phone"></i> (555) 555-5555</li>
                                <li><i class="fa fa-envelope"></i> test@acme.test</li>
                            </ul>
                        </div>
                        <div class="contact">
                            <h3>Email Us</h3>
                            <p>
                                <label>Name</label>
                                <input type="text" name="name"></input>
                            </p>
                            <p>
                                <label>Company</label>
                                <input type="text" name="company"></input>
                            </p>
                            <p>
                                <label>Email Address</label>
                                <input type="email" name="email"></input>
                            </p>
                            <p>
                                <label>Phone Number</label>
                                <input type="text" name="phone"></input>
                            </p>
                            <p class="full">
                                <label>Message</label>
                                <textarea name="message" rows="5"></textarea>
                            </p>
                            <p class="full">
                                <button type="submit" onClick={() => this.sendMessage()}>Submit</button>
                            </p>
                        </div>
                    </div>
                </div>
                <Footer></Footer>
            </div>
        )
    }
}
