import 'bootstrap/dist/css/bootstrap.css';
import React,{Component} from 'react';
import { Link } from 'react-router-dom';
import Navbar from './../navbar/Navbar.js'
import Footer from './../footer/Footer.js'
import nodemailer from 'nodemailer';
import axios from 'axios';
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Carousel from "react-bootstrap/Carousel";
import GoogleMap from './../map/Map.js'
import Form from "react-bootstrap/Form";
import './booking.css';

export default class booking extends Component{

    state = {
        allScreening: [],
        email: "",
        screenTime: "",
        screenId: "",
        chosenScreening: [],
        chosenSeats: [],
      }

    componentDidMount() {
        console.log(this.props.match.params.id)
        const title = (this.props.match.params.id);
        axios.get(`http://localhost:5000/api/screenings/${title}`)
        .then(res => {
            const allScreening = res.data;
            this.setState({ allScreening });
        })
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
        console.log(this.state)

      };

      postBooking = (event) => {
        event.preventDefault();
        const title = this.props.match.params.id;
        console.log(this.state.screenTime)
        console.log(this.state.email)
        console.log(title)
        // axios
        //   .post(`http://localhost:5000/api/comments`, {
        //     commentBody: this.state.body,
        //     name: this.state.name,
        //     email: this.state.email,
        //     movieId: ID,
        //   })
        //   .then((res) => {
        //     this.setState({
        //       name: "",
        //       body: "",
        //       email: "",
        //     });
        //   });
      };

      getSeats = (screenID) => {
        axios.get(`http://localhost:5000/api/screenings/byId/${screenID}`)
        .then(res => {
            const chosenScreening = res.data;
            this.setState({ chosenScreening });
        })
      };

      getChckeboxValue(event) {
        this.setState({ chosenSeats: this.state.chosenSeats.concat(event.target.value) })
        console.log(this.state)
    }

    render(){
        return(
            <div>
                <Navbar></Navbar>
                <div style={{ backgroundColor: "black", color: "white" }}>
                <div
                class="container"
                style={{
                    backgroundColor: "black",
                    color: "white",
                    paddingTop: "80px",
                    paddingBottom: "80px",
                }}
                >
                <div class="row" style={{ paddingLeft: "30px" }}></div>
                    <Form onSubmit={this.postBooking}>
                    <Form.Group controlId="exampleForm.ControlInput1">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control type="email" placeholder="name@example.com" name="email" onChange={this.handleChange} value={this.state.email} required />
                    </Form.Group>
                    Select a screen time:
                    <ul>
                    {
                        this.state.allScreening.map(screen	=>
                            <li>
                                {screen.screeningTime} <button onClick={() => this.getSeats(screen._id)}>Get seats</button>
                            </li>
                        )
                    }
                    </ul>
                    <Form.Group controlId="exampleForm.ControlInput1">

                        {
                        this.state.chosenScreening.length>0 && this.state.chosenScreening[0].seats.map((seat, index1) => {
                                    return(
                                        <center>
                                        <div class="plane">
                                            <ol class="cabin fuselage">
                                                <li class="row row--1">
                                                    <ol class="seats" type="A">
                                                        {seat.map((s, index)=> 
                                                            <li class="seat">
                                                                <Form.Check inline label={s} id={`${index1}.${index}`} value={`${index1}.${index}`} name="chosenSeats" onClick={this.getChckeboxValue.bind(this)} />
                                                            </li>
                                                        )}
                                                    </ol>
                                                </li>
                                            </ol>
                                        </div>
                                        </center>
                                        )
                            }
                        )
                    }
                    </Form.Group>
                    <button type="submit" class="btn btn-secondary btn-lg">
                        Make Booking
                    </button>
                    </Form>
                </div>
                </div>
                <Footer/>
            </div>
        )
    }
}
