import 'bootstrap/dist/css/bootstrap.css';
import React,{Component} from 'react';
import './App.css';

export default class Footer extends Component{
    constructor(){
        super()
        this.state={
            records:[
                {
                    "movieID":1,
                    "name":"Harry Potter",
                    "image": "media/harrypotter.jpg",
                    "description": "When his brother is killed in a robbery, paraplegic Marine Jake Sully decides to take his place in a mission on the distant world of Pandora. There he learns of greedy corporate figurehead Parker Selfridge's intentions of driving off the native humanoid Navi in order to mine for the precious material scattered throughout their rich woodland. In exchange for the spinal surgery that will fix his legs, Jake gathers knowledge, of the Indigenous Race and their Culture, for the cooperating military unit spearheaded by gung-ho Colonel Quaritch, while simultaneously attempting to infiltrate the Navi people with the use of an avatar identity. While Jake begins to bond with the native tribe and quickly falls in love with the beautiful alien Neytiri, the restless Colonel moves forward with his ruthless extermination tactics, forcing the soldier to take a stand - and fight back in an epic battle for the fate of Pandora.",
                    "releaseDate": "10-01-2009"
                },
            ]
        }
    }
    render(){
        return (
            <div class="container-fluid" style={{backgroundColor: "black", color: "white", paddingTop: "80px", paddingBottom: "80px"}}>
                <div class="row">
                    <div class="col">
                    <img src="media/logo.png" style={{width: "150px", height: "70px"}}/>
                    </div>
                    <div class="col">
                        Opening times:<br/>
                        Sun-Thur    11am - 8pm<br/>
                        Fri - Sat   11am - 11pm<br/> 
                    </div>
                    <div class="col">
                        Contact Us<br/>
                        Email: Anaeem@QA.com<br/>
                        Phone: 0800 101 4242
                    </div>
                    <div class="col-4">
                    </div>
                    <div class="col">
                        Social:
                        Twitter: @QA_Cinema
                        Instagram: @QACinemaLondon
                    </div>
                </div>
            </div>
            // <div class="row row-cols-4">
            //     <div class="card" style={{width: "8rem", hight: "8rem", backgroundColor: "black"}} >
            //     <img src={data.image} class="card-img-top" alt={data.name}/>
            //     <div class="card-body">
            //     <p class="card-text">{data.name}</p>
            //     </div>
            // </div>
            // </div>
        )
    }   
}
