import 'bootstrap/dist/css/bootstrap.css';
import React,{Component} from 'react';
import './App.css';
import { Link } from 'react-router-dom';
import Navbar from './Navbar.js'
import Footer from './Footer.js'
import nodemailer from 'nodemailer';
import axios from 'axios';
import Form from "react-bootstrap/Form";

export default class Contact extends React.Component {
    constructor(props) {
      super(props);
      this.state = {name: '', email: '', message: '' };
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    render() {
        return (
            <Form onSubmit={this.handleSubmit}>
                  <legend>Write us a message and we will get back to you</legend>
                  <Form.Group controlId="exampleForm.ControlInput1">
                    <Form.Label>Email address</Form.Label>
                    <Form.Control type="email" placeholder="name@example.com" name="email" onChange={this.handleChange} value={this.state.email} required />
                  </Form.Group>
                  <Form.Group controlId="exampleForm.ControlInput1">
                    <Form.Label>Name</Form.Label>
                    <Form.Control type="text" placeholder="Jhon Smith" name="name" onChange={this.handleChange} placeholder="Name" value={this.state.name} required/>
                  </Form.Group>
                  <Form.Group controlId="exampleForm.ControlTextarea1">
                    <Form.Label>Message</Form.Label>
                    <Form.Control as="textarea" rows="3" name="message" value={this.state.message} onChange={this.handleChange}/>
                  </Form.Group>
                  <button type="submit" class="btn btn-secondary btn-lg">
                    Send Message
                  </button>
                </Form>
        //   <form className="test-mailing">
        //     <h1>Let's see if it works</h1>
        //     <input type="text" id="messageName" placeholder="Your Name" required onChange={this.handleChange} value={this.state.name}></input>
        //     <input type="email" id="messageEmail" placeholder="example@provider.com" required onChange={this.handleChange} value={this.state.name}></input>
        //     <div>
        //       <textarea
        //         id="test-mailing"
        //         name="test-mailing"
        //         onChange={this.handleChange}
        //         placeholder="Post some lorem ipsum here"
        //         required
        //         value={this.state.feedback}
        //         style={{width: '100%', height: '150px'}}
        //       />
        //     </div>
        //     <input type="button" value="Submit" className="btn btn--submit" onClick={this.handleSubmit} />
        //   </form>
        )
      }

      handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
      };

    //   handleChange(event) {
    //     this.setState({feedback: event.target.value})
    //   }

    handleSubmit (event) {
        const templateId = 'template_dvd0l3u';
        this.sendFeedback(templateId, {message_html: this.state.message, from_name: this.state.name, reply_to: this.state.email})
    }

    sendFeedback (templateId, variables) {
        window.emailjs.send(
        'service_41woeob', templateId,
        variables
        ).then(res => {
            console.log('Email successfully sent!')
        })
        // Handle errors here however you like, or use a React error boundary
        .catch(err => console.error('Oh well, you failed. Here some thoughts on the error that occured:', err))
    }
}
