import 'bootstrap/dist/css/bootstrap.css';
import React,{Component} from 'react';
import axios from 'axios';
import './App.css';
import {Link} from 'react-router-dom';
import Navbar from './Navbar.js'
import Footer from './Footer.js'
import Form from 'react-bootstrap/Form';


class Comment extends React.Component {

  constructor(props){
    super(props)
  this.state = {
    movies: [],
    comments: [],
    name: "",
    body: "",
    email: ""
    }
  }

  handleChange = (event) => {
    this.setState({[event.target.name]: event.target.value})
  }
  componentDidMount() {
    window.scrollTo(0, 0)
    const ID = this.props.match.params.id
		axios.get(`http://localhost:5000/api/movies/${ID}`)
		  .then(res => {
			const movies = res.data;
			this.setState({ movies });
      })

    	axios.get(`http://localhost:5000/api/comments/${ID}`)
		  .then(res => {
			const comments = res.data;
			this.setState({ comments });
      })
    }

    componentDidUpdate(){
      const ID = this.props.match.params.id
		  axios.get(`http://localhost:5000/api/comments/${ID}`)
		  .then(res => {
			const comments = res.data;
			this.setState({ comments });
      })
    }

    postComment = (event) => {
      event.preventDefault()
      const ID = this.props.match.params.id;
      axios.post(`http://localhost:5000/api/comments`, {
        commentBody: this.state.body,
        name: this.state.name,
        email: this.state.email,
        movieId: ID
      }).then(res => {
        this.setState({
          name: "",
          body: "",
          email: ""
        })
      })
    }

  render() {
    return (
      <div>
        <Navbar></Navbar>
        <div class="container-fluid" style={{backgroundColor: "black", color: "white", paddingTop: "80px", paddingBottom: "80px",}}>
            <div class="row" style={{ paddingLeft: "30px" }}>
              <div class="col-4">
                <img src={`../${this.state.movies.image}`} style={{ height: "700px", width: "400px" }}
                  alt={this.state.movies.title}
                />
              </div>
              <div class="col-5">
                {this.state.movies.description}
                <br />
              </div>
              <div class="col-3">
                <h2>{this.state.movies.title}</h2>
                <br />
                Age Rating: <br />
                {this.state.movies.ageRating}
                <br />
                <br />
                Actors: <br />
                {this.state.movies.cast}
                <br />
                <br />
                Director: <br />
                {this.state.movies.director}
                <br />
                <br />
                Genre: <br />
                {this.state.movies.genre}
                <br />
                <br />
                Duration: <br />
                {this.state.movies.runningTime} minites
                <br />
                <br />
              </div>
            </div>
        </div>
        <div style={{ backgroundColor: "black", color: "white" }}>
          <center>
            <h1>Ratings & Reviews</h1>
          </center>
          <div class="container">
            <div class="row">
              <div class="col-sm-4">
              <Form>
  <Form.Group controlId="exampleForm.ControlInput1">
    <Form.Label>Email address</Form.Label>
    <Form.Control type="email" placeholder="name@example.com" />
  </Form.Group>
  <Form.Group controlId="exampleForm.ControlSelect1">
    <Form.Label>Example select</Form.Label>
    <Form.Control as="select">
      <option>1</option>
      <option>2</option>
      <option>3</option>
      <option>4</option>
      <option>5</option>
    </Form.Control>
  </Form.Group>
  <Form.Group controlId="exampleForm.ControlSelect2">
    <Form.Label>Example multiple select</Form.Label>
    <Form.Control as="select" multiple>
      <option>1</option>
      <option>2</option>
      <option>3</option>
      <option>4</option>
      <option>5</option>
    </Form.Control>
  </Form.Group>
  <Form.Group controlId="exampleForm.ControlTextarea1">
    <Form.Label>Example textarea</Form.Label>
    <Form.Control as="textarea" rows="3" />
  </Form.Group>
</Form>
                <form onSubmit={this.postComment}>
                    <legend>Leave a comment</legend>
                    <textarea
                      type="text"
                      name = "body"
                      value = {this.state.body}
                      placeholder="Add to the conversation"
                      onChange={this.handleChange}
                      required
                    ></textarea>
                    <br />
                    <input type="text" name = "name" onChange={this.handleChange} placeholder="Name" value={this.state.name} required />
                    <input
                      type="email"
                      name ="email"
                      value={this.state.email}
                      placeholder="Email"
                      onChange={this.handleChange}
                      required
                    />
                    <br />
                  <button
                    type="submit"
                    class="btn btn-secondary btn-lg"
                  >
                    Post Comment
                  </button>
                  </form>
              </div>
              <div class="col-sm-8">
                <div class="col-sm-14">
                  <div class="list-group">
                  { this.state.comments.reverse().map(comment	=>
                  <a class="list-group-item list-group-item-action active" style = {{marginBottom: "10px"}}>

                  <div class="d-flex w-100 justify-content-between">
                  <h5 class="mb-1">{comment.name}</h5>
                    <small>{`${new Date(comment.date).toLocaleTimeString()} ${new Date(comment.date).toLocaleDateString()}`}</small>
                  </div>
                  <p class="mb-1">
                    {comment.commentBody}
                  </p>
                  <small>{comment.email}</small>
                  </a>
                  )
                  }
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        <Footer></Footer>
