import 'bootstrap/dist/css/bootstrap.css';
import React,{Component} from 'react';
import { Link } from 'react-router-dom';
import Navbar from './../navbar/Navbar.js'
import Footer from './../footer/Footer.js'
import nodemailer from 'nodemailer';
import axios from 'axios';
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Carousel from "react-bootstrap/Carousel";
import GoogleMap from './../map/Map.js'
import Form from "react-bootstrap/Form";

export default class booking extends Component{

    state = {
        allScreening: [],
        email: "",
        screenTime: "",
        screenId: "",
        chosenScreening: [],
      }

    componentDidMount() {
        console.log(this.props.match.params.id)
        const title = (this.props.match.params.id);
        axios.get(`http://localhost:5000/api/screenings/${title}`)
        .then(res => {
            const allScreening = res.data;
            this.setState({ allScreening });
        })
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });

      };

      postBooking = (event) => {
        event.preventDefault();
        const title = this.props.match.params.id;
        console.log(this.state.screenTime)
        console.log(this.state.email)
        console.log(title)
        // axios
        //   .post(`http://localhost:5000/api/comments`, {
        //     commentBody: this.state.body,
        //     name: this.state.name,
        //     email: this.state.email,
        //     movieId: ID,
        //   })
        //   .then((res) => {
        //     this.setState({
        //       name: "",
        //       body: "",
        //       email: "",
        //     });
        //   });
      };

      getSeats = (screenID) => {
        axios.get(`http://localhost:5000/api/screenings/byId/${screenID}`)
        .then(res => {
            const chosenScreening = res.data;
            this.setState({ chosenScreening });
        })
        console.log(this.state.screenId)
        console.log(this.state.chosenScreening)

      };

    render(){
        return(
            <div>
                <Navbar></Navbar>
                <Form onSubmit={this.postBooking}>
                <Form.Group controlId="exampleForm.ControlInput1">
                    <Form.Label>Email address</Form.Label>
                    <Form.Control type="email" placeholder="name@example.com" name="email" onChange={this.handleChange} value={this.state.email} required />
                </Form.Group>
                <h3>Select a screen time</h3>
                {
                    this.state.allScreening.map(screen	=>
                    <div>
                        <h4>{screen.screeningTime}</h4> <button onClick={() => this.getSeats(screen._id)}>Get seats</button>
                    </div>
                    )
                }
                <h3>Select seats</h3>
                <table>
                {
                    this.state.chosenScreening.length>0 && this.state.chosenScreening[0].seats.map(seat => {
                            // return(
                            //     <tr>
                            //         {seat.map(s => <td>{s}</td>)}
                            //     </tr>
                            //     )
                                return(
                                    <div class="plane">
                                        <ol class="cabin fuselage">
                                            <li class="row row--1">
                                                <ol class="seats" type="A">
                                                    {seat.map(s => 
                                                        <li class="seat">
                                                            <input type="checkbox" id="1A" />
                                                            <label for="1A">1A</label>
                                                        </li>
                                                    )}
                                                </ol>
                                            </li>
                                        </ol>
                                    </div>
                                    )
                        }
                    )
                }
                </table>
                <Form.Group controlId="exampleForm.ControlSelect1">
                    <Form.Label>Select a screen time</Form.Label>
                    <Form.Control as="select" name="screenTime" onChange={this.handleChange} value={this.state.screenTime} required>
                        <option>Select</option>
                        {
                        this.state.allScreening.map(screen	=>
                            <option>{screen.screeningTime}</option>
                        )
                        }
                    </Form.Control>
                </Form.Group>
                <button type="submit" class="btn btn-secondary btn-lg">
                    Make Booking
                  </button>
                </Form>
                <Footer></Footer>
            </div>
        )
    }
}
