import 'bootstrap/dist/css/bootstrap.css';
import React,{Component} from 'react';
import './App.css';
import { Link } from 'react-router-dom';
import Navbar from './Navbar.js'
import Footer from './Footer.js'
import nodemailer from 'nodemailer';
import axios from 'axios';
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

export default class About extends Component{


    render(){
        return(
            <div>
                <Navbar></Navbar>
                <div style={{backgroundColor: "black", color: "white"}}>
                    <div class="container">
                        <div class="row">
                            <div class="col">
                                <center>
                                <h1>QA Cinema</h1><br></br>
                                <h4>We are a new Cinema that have been established in 2020 with the goal of providing users the oppertunity to book the latest
                                    block buster movies for cheap prices. With our nice new cinema located in London you can watch the most talked about movies 
                                    in great comfort and style. With lots of seats to choose from and different sized screans you will have a unique experiance 
                                    every time you visit.</h4><br></br> 
                                    <h4>Booking has never been easy with our eay to use online booking system where you can choose a time and seat that suits you best.</h4>
                                </center>
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-sm">
                                One of three columns
                            </div>
                            <div class="col-sm">
                                One of three columns
                            </div>
                            <div class="col-sm">
                                One of three columns
                            </div>
                        </div>
                    </div>
                </div>
                <Footer></Footer>
            </div>
        )
    }
}
