import 'bootstrap/dist/css/bootstrap.css';
import React,{Component} from 'react';
import './App.css';


export default class Homebody extends Component{
	constructor(){
		super()
		this.Addrecord=this.Addrecord.bind(this)
		this.delete=this.delete.bind(this)

		this.state={
			records:[
				{
					"movieID":1,
					"name":"Harry Potter",
					"src": "media/harrypotter.png"
				},
				{
					"movieID":2,
					"name":"The Avengers",
					"image":"media/avengers.jpg"
				},
				{
                    "movieID":3,
					"name":"Batman",
					"image":"media/batman.png"
				},
				{
					"movieID":4,
					"name":"Avatar",
					"image":"media/avatar.jpg"
				},

			]
			}
    }

	delete(r){
		this.setState({
			records: this.state.records.filter(function(data){
				if (data.regno==r){
					return false
				}else{
					return true
				}
			})
		})
	}
	Addrecord(){
		this.setState({
			records:this.state.records.concat({
					"regno":121,
					"name":"Pater2",
					"marks":902
				})	
		})
	}
	render(){
		return (
			<div style={{backgroundColor: "black", color: "white"}}>
                <center><img src="/media/wallpaper.jpg" style={{hight: "600px", width: "1000px"}}alt="Wallpaper"/></center>
                <h1>TEST 1</h1>
                {
                this.state.records.map(data	=>
                    <div class="card" style={{width: "18rem", hight: "40rem", backgroundColor: "black"}} >
                    <img src={data.image} class="card-img-top" alt={data.name}/>
                    <div class="card-body">
                <p class="card-text">{data.name}</p>
                    </div>
                </div>
                )
                }

                <div class="card" style={{width: "18rem", hight: "40rem", backgroundColor: "black"}} >
                    <img src="..." class="card-img-top" alt="..."/>
                    <div class="card-body">
                        <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                    </div>
                </div>

					{/* <br/
					<br/>	
					<input type="button" value="New Record" onClick={this.Addrecord}/>
					<br/>
					Total Number of Records( { this.state.records.length} )
					<table>
						<tr>
							<td> regno </td>
							<td> name </td>
							<td> marks </td>
						</tr>	
				 	{
				 		this.state.records.map(data	=>
				 		<tr> 
				 			<td> {data.regno}  </td>
				 			<td> {data.name}  </td>
				 			<td> {data.marks} </td>
				 			<td> {data.marks*100/150} </td>
				 			<td> <input type="button" value="delete" onClick={() => this.delete(data.regno)} /> </td>
				 		</tr>
				 		)
				 	}
				 	</table> */}
			</div>
		)
	}
}
