import 'bootstrap/dist/css/bootstrap.css';
import React,{Component} from 'react';
import { Link } from 'react-router-dom';
import Navbar from './../navbar/Navbar.js'
import Footer from './../footer/Footer.js'
import nodemailer from 'nodemailer';
import axios from 'axios';
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Carousel from "react-bootstrap/Carousel";
import GoogleMap from './../map/Map.js'
import Form from "react-bootstrap/Form";

export default class booking extends Component{

    state = {
        screening: [],
        email: "",
        screenTime: "",
      }

    componentDidMount() {
        console.log(this.props.match.params.id)
        const title = (this.props.match.params.id);
        axios.get(`http://localhost:5000/api/screenings/${title}`)
        .then(res => {
            const screening = res.data;
            this.setState({ screening });
        })
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
      };

      postBooking = (event) => {
        event.preventDefault();
        const title = this.props.match.params.id;
        console.log(this.state.screenTime)
        console.log(this.state.email)
        console.log(title)
        // axios
        //   .post(`http://localhost:5000/api/comments`, {
        //     commentBody: this.state.body,
        //     name: this.state.name,
        //     email: this.state.email,
        //     movieId: ID,
        //   })
        //   .then((res) => {
        //     this.setState({
        //       name: "",
        //       body: "",
        //       email: "",
        //     });
        //   });
      };

    render(){
        return(
            <div>
                <Navbar></Navbar>
                <Form onSubmit={this.postBooking}>
                <Form.Group controlId="exampleForm.ControlInput1">
                    <Form.Label>Email address</Form.Label>
                    <Form.Control type="email" placeholder="name@example.com" name="email" onChange={this.handleChange} value={this.state.email} required />
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlSelect1">
                    <Form.Label>Select a screen time</Form.Label>
                    <Form.Control as="select" name="screenTime" onChange={this.handleChange} value={this.state.screenTime} required>
                        <option>Select</option>
                        {
                        this.state.screening.map(screen	=>
                            <option>{screen.theaterId} - {screen.screeningTime}</option>
                        )
                        }
                    </Form.Control>
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlSelect2">
                    <Form.Label>Example multiple select</Form.Label>
                    <Form.Control as="select" multiple>
                        {
                        this.state.screening.map(screen	=>
                            <option>{screen.seats[1]}</option>
                        )}
                    </Form.Control>
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlTextarea1">
                    <Form.Label>Example textarea</Form.Label>
                    <Form.Control as="textarea" rows="3" />
                </Form.Group>
                <button type="submit" class="btn btn-secondary btn-lg">
                    Make Booking
                  </button>
                </Form>
                <Footer></Footer>
            </div>
        )
    }
}
